function makeCall(){
    MyLocationList = [];
    myTimeList = [];
    $.get( "https://telegov.njportal.com/njmvc/AppointmentWizard/15", function( data ) {
    	d = data
    	timeDataString = d.substring(d.indexOf("timeData")+ 11);
    	timeDataString = timeDataString.slice(0, timeDataString.indexOf("}]")+2);
    	timeDataList = JSON.parse(timeDataString);
    	
    	locationDataString = d.substring(d.indexOf("locationData")+ 15);
    	locationDataString = locationDataString.slice(0, locationDataString.indexOf("}];")+2);
    	locationDataList = JSON.parse(locationDataString);
    	
    		
    	
    	for(var i=0; i<locationDataList.length;i++){
    		for(var j=0; j<myZipCodes.length;j++){
    			if(locationDataList[i].Zip == myZipCodes[j]){
    				myLocationList.push(locationDataList[i]);
    				
    				for(var k=0; k<timeDataList.length;k++){
    					if(timeDataList[k].LocationId == locationDataList[i].Id){
    						timeDataList[k].ZIP = locationDataList[i].Zip;
    						myTimeList.push(timeDataList[k]);
    					}
    				}
    			}
    		}
    		
    	}
    	
    	
    	for(var i=0 ;i<myTimeList.length;i++){
    		currentItem = myTimeList[i];
    		if(currentItem.FirstOpenSlot.includes("Next Available: 01/")){
                var indexOfDay = currentItem.FirstOpenSlot.indexOf("Next Available: 01/")+19;
    		    day = currentItem.FirstOpenSlot.substr(indexOfDay, 2);
    		    if(day > 15){
    		        console.log("FOUND, ZIP="+currentItem.ZIP);
    			    result = true;
    		    }
    			
    		}
    	}
    	if(!result){
    	    console.log("Nothing found. Will Try Again in 15 Seconds...");
    	    setTimeout(function(){
    	        console.log("Will Try in 5 Seconds.");
    	        setTimeout(function(){
        	        console.log("Trying again..");
        	        makeCall()
        	    },5000);
    	    },10000);
    	    
    	}
    	else{
    	    beepRepeat();
    	}
    });
}
function beep(){
    var snd = new Audio("data:audio/wav;base64,");  
    snd.play();
}
function beepRepeat(){
    beep();
    setTimeout(function(){
        beepRepeat();
    }, 500);
}
function init(zips = ["07114","07047","07470","07644","07002","07505"]){
    myZipCodes = zips;
    makeCall();
}
var d;
var timeDataString;
var timeDataList;
var locationDataString;
var locationDataList;
var currentItem;
var myZipCodes = ["07114","07047","07470","07644","07002","07505"];
var myLocationList = [];
var myTimeList = [];
var result = false;
